  NEW.completed_steps := (
    CASE WHEN NEW.account_created_at IS NOT NULL THEN 1 ELSE 0 END +
    CASE WHEN NEW.profile_completed_at IS NOT NULL THEN 1 ELSE 0 END +
    CASE WHEN NEW.first_meeting_synced_at IS NOT NULL THEN 1 ELSE 0 END +
