    LANGUAGE "sql" STABLE
    SET "search_path" TO ''
    AS $$
  SELECT
    COUNT(*)::INTEGER AS total_sent,
    COUNT(*) FILTER (WHERE status IN ('replied', 'recovered'))::INTEGER AS total_replied,
    COUNT(*) FILTER (WHERE status = 'recovered')::INTEGER AS total_recovered,
    CASE
