      ELSE 0
    END AS response_rate_percent,
    CASE
      WHEN COUNT(*) > 0 THEN ROUND((COUNT(*) FILTER (WHERE status = 'recovered')::NUMERIC / COUNT(*)::NUMERIC) * 100)::INTEGER
      ELSE 0
    END AS recovery_rate_percent
  FROM interventions
  WHERE user_id = user_id_param
    AND status <> 'pending'
    AND sent_at IS NOT NULL;
