CREATE OR REPLACE FUNCTION "public"."get_org_team_members"("p_org_id" "uuid") RETURNS TABLE("user_id" "uuid", "email" "text", "full_name" "text", "meeting_count" bigint, "indexed_count" bigint)
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO ''
    AS $$
  BEGIN
    RETURN QUERY
    SELECT
      om.user_id,
      COALESCE(fi.fathom_user_email, u.email) as email,
      COALESCE(u.raw_user_meta_data->>'full_name', u.raw_user_meta_data->>'name',
  split_part(COALESCE(fi.fathom_user_email, u.email), '@', 1)) as full_name,
      COUNT(DISTINCT m.id) FILTER (WHERE m.transcript_text IS NOT NULL AND m.transcript_text
  != '') as meeting_count,
      COUNT(DISTINCT mfi.meeting_id) FILTER (WHERE mfi.status = 'indexed') as indexed_count
    FROM organization_memberships om
    INNER JOIN auth.users u ON om.user_id = u.id
    LEFT JOIN fathom_integrations fi ON om.user_id = fi.user_id AND fi.is_active = true
    LEFT JOIN meetings m ON m.owner_user_id = om.user_id
    LEFT JOIN meeting_file_search_index mfi ON m.id = mfi.meeting_id
    WHERE om.org_id = p_org_id
    GROUP BY om.user_id, fi.fathom_user_email, u.email, u.raw_user_meta_data
    HAVING COUNT(DISTINCT m.id) FILTER (WHERE m.transcript_text IS NOT NULL AND
  m.transcript_text != '') > 0
    ORDER BY full_name;
  END;
  $$;


