    AS $$
DECLARE
  v_user_id UUID;
BEGIN
  CASE p_activity_type
    WHEN 'meeting' THEN
      SELECT owner_user_id INTO v_user_id
      FROM meetings
      WHERE id = p_activity_id;

    WHEN 'call' THEN
      SELECT owner_user_id INTO v_user_id
      FROM calls
      WHERE id = p_activity_id;

    WHEN 'activity' THEN
      SELECT user_id INTO v_user_id
      FROM activities
      WHERE id = p_activity_id;

    ELSE
      RETURN NULL;
  END CASE;

  RETURN v_user_id;
END;
