--

CREATE OR REPLACE FUNCTION "public"."rename_user_organization"("p_new_name" "text") RETURNS TABLE("success" boolean, "org_id" "uuid", "org_name" "text", "error_message" "text")
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'public'
    AS $$
DECLARE
  v_org_id UUID;
  v_clean_name TEXT;
BEGIN
  SELECT om.org_id INTO v_org_id
  FROM organization_memberships om
  WHERE om.user_id = auth.uid()
    AND om.role = 'owner'
  ORDER BY om.created_at ASC
  LIMIT 1;

  IF v_org_id IS NULL THEN
    RETURN QUERY SELECT false, NULL::UUID, NULL::TEXT, 
      'No organization found where you are the owner'::TEXT;
    RETURN;
  END IF;

  v_clean_name := TRIM(p_new_name);

  IF LENGTH(v_clean_name) < 1 THEN
    RETURN QUERY SELECT false, v_org_id, NULL::TEXT, 
      'Organization name cannot be empty'::TEXT;
    RETURN;
  END IF;

  IF LENGTH(v_clean_name) > 100 THEN
    RETURN QUERY SELECT false, v_org_id, NULL::TEXT, 
      'Organization name too long (max 100 characters)'::TEXT;
    RETURN;
  END IF;

  UPDATE organizations
  SET name = v_clean_name, updated_at = NOW()
  WHERE id = v_org_id;

  RETURN QUERY SELECT true, v_org_id, v_clean_name, NULL::TEXT;
END;
$$;


