    LANGUAGE "plpgsql" SECURITY DEFINER
    AS $$
DECLARE
  v_result_id UUID;
  v_skill_name TEXT;
BEGIN
  -- Get skill name from platform_skills
  SELECT COALESCE(
    ps.frontmatter->>'name',
    INITCAP(REPLACE(ps.skill_key, '-', ' '))
  ) INTO v_skill_name
  FROM platform_skills ps
  WHERE ps.id = p_platform_skill_id;

