CREATE OR REPLACE VIEW "public"."activities_with_profile" WITH ("security_invoker"='true') AS
 SELECT "a"."id",
    "a"."user_id",
    "a"."type",
    "a"."status",
    "a"."priority",
    "a"."client_name",
    "a"."sales_rep",
    "a"."details",
    "a"."amount",
    "a"."date",
    "a"."created_at",
    "a"."updated_at",
    "a"."avatar_url",
    "a"."quantity",
    "a"."contact_identifier",
    "a"."contact_identifier_type",
    "a"."is_processed",
    "a"."deal_id",
    "a"."is_split",
    "a"."original_activity_id",
    "a"."split_percentage",
    "a"."owner_id",
    "a"."contact_id",
    "a"."company_id",
    "a"."subject",
    "a"."auto_matched",
    "a"."outbound_type",
    "a"."proposal_date",
    "a"."is_rebooking",
    "a"."is_self_generated",
    "a"."sale_date",
    "a"."execution_order",
    "a"."meeting_id",
    "a"."next_actions_generated_at",
    "a"."next_actions_count",
    "a"."savvycal_booking_id",
    "a"."savvycal_link_id",
    "a"."clerk_org_id",
    "p"."id" AS "profile_id",
    COALESCE((("p"."first_name" || ' '::"text") || "p"."last_name"), "p"."email") AS "profile_full_name",
    "p"."avatar_url" AS "profile_avatar_url"
   FROM ("public"."activities" "a"
     LEFT JOIN "public"."profiles" "p" ON (("a"."user_id" = "p"."id")));


