--
-- Name: cron_jobs_status; Type: VIEW; Schema: public; Owner: postgres
--

CREATE OR REPLACE VIEW "public"."cron_jobs_status" WITH ("security_invoker"='true') AS
 SELECT "j"."jobid",
    "j"."jobname",
    "j"."schedule",
    "j"."active",
    "j"."nodename",
    "s"."display_name",
    "s"."description",
    "s"."category",
    "s"."is_monitored",
    "s"."alert_on_failure",
    ( SELECT "jsonb_build_object"('runid', "rd"."runid", 'status', "rd"."status", 'start_time', "rd"."start_time", 'end_time', "rd"."end_time", 'return_message', "rd"."return_message") AS "jsonb_build_object"
           FROM "cron"."job_run_details" "rd"
          WHERE ("rd"."jobid" = "j"."jobid")
          ORDER BY "rd"."start_time" DESC
         LIMIT 1) AS "last_run",
    ( SELECT ("count"(*))::integer AS "count"
           FROM "cron"."job_run_details" "rd"
          WHERE (("rd"."jobid" = "j"."jobid") AND ("rd"."status" = 'failed'::"text") AND ("rd"."start_time" > ("now"() - '24:00:00'::interval)))) AS "failures_last_24h",
    ( SELECT ("count"(*))::integer AS "count"
           FROM "cron"."job_run_details" "rd"
          WHERE (("rd"."jobid" = "j"."jobid") AND ("rd"."start_time" > ("now"() - '24:00:00'::interval)))) AS "runs_last_24h"
   FROM ("cron"."job" "j"
     LEFT JOIN "public"."cron_job_settings" "s" ON (("s"."job_name" = "j"."jobname")));


