    "date_trunc"('month'::"text", "os"."started_at") AS "cohort_month",
    "date_trunc"('week'::"text", "os"."started_at") AS "cohort_week",
    ("os"."status" = ANY (ARRAY['active'::"text", 'trialing'::"text"])) AS "is_active",
    ("os"."status" = 'trialing'::"text") AS "is_trialing",
    "os"."created_at",
