 SELECT COALESCE("sum"("normalized_mrr_cents"), (0)::bigint) AS "total_mrr_cents",
    "count"(*) FILTER (WHERE "is_active") AS "active_subscriptions",
    "count"(*) FILTER (WHERE "is_trialing") AS "trialing_subscriptions",
